package xyz.itwill.dto;

public class BestDTO {
	private int btNo;
	private String btName;
	private String btImg;
	private int btPrice;
	private String btInfo;
	private int btStock;
	
	public BestDTO() {
		// TODO Auto-generated constructor stub
	}

	public int getBtNo() {
		return btNo;
	}

	public void setBtNo(int btNo) {
		this.btNo = btNo;
	}

	public String getBtName() {
		return btName;
	}

	public void setBtName(String btName) {
		this.btName = btName;
	}

	public String getBtImg() {
		return btImg;
	}

	public void setBtImg(String btImg) {
		this.btImg = btImg;
	}

	public int getBtPrice() {
		return btPrice;
	}

	public void setBtPrice(int btPrice) {
		this.btPrice = btPrice;
	}

	public String getBtInfo() {
		return btInfo;
	}

	public void setBtInfo(String btInfo) {
		this.btInfo = btInfo;
	}

	public int getBtStock() {
		return btStock;
	}

	public void setBtStock(int btStock) {
		this.btStock = btStock;
	}
}
